double convexHullPerimeter(std::vector<Point> const& points) {
    if (points.size() < 3) {
        return 0.0;
    }

    std::vector<Point> pointsSorted = points;
    std::sort(pointsSorted.begin(), pointsSorted.end(), [](const Point& a, const Point& b) {
        return a.x < b.x || (a.x == b.x && a.y < b.y);
    });

    std::vector<Point> ans(2 * pointsSorted.size());

    int n = points.size();
    int k = 0;

    // Build lower hull
    for (int i = 0; i < n; ++i) {
        while (k >= 2 && 
               (ans[k-1].x - ans[k-2].x) * (pointsSorted[i].y - ans[k-2].y) <= 
               (ans[k-1].y - ans[k-2].y) * (pointsSorted[i].x - ans[k-2].x)) {
            k--;
        }
        ans[k++] = pointsSorted[i];
    }

    // Build upper hull
    for (int i = n - 1, t = k + 1; i > 0; --i) {
        while (k >= t && 
               (ans[k-1].x - ans[k-2].x) * (pointsSorted[i-1].y - ans[k-2].y) <= 
               (ans[k-1].y - ans[k-2].y) * (pointsSorted[i-1].x - ans[k-2].x)) {
            k--;
        }
        ans[k++] = pointsSorted[i-1];
    }

    ans.resize(k - 1);
    int size = ans.size();
    double perimeter = 0.0;

    #pragma omp parallel for reduction(+:perimeter)
    for (int i = 0; i < size - 1; i++) {
        double dx = ans[i+1].x - ans[i].x;
        double dy = ans[i+1].y - ans[i].y;
        perimeter += std::sqrt(dx*dx + dy*dy);
    }

    double dx = ans[0].x - ans[size-1].x;
    double dy = ans[0].y - ans[size-1].y;
    perimeter += std::sqrt(dx*dx + dy*dy);

    return perimeter;
}
