bool contains(std::vector<int> const& x, int target) {
    size_t n = x.size();
    if (n == 0) {
        return false;
    }

    // Small vectors: use sequential search to avoid OpenMP overhead
    if (n < 100) {
        for (size_t i = 0; i < n; ++i) {
            if (x[i] == target) {
                return true;
            }
        }
        return false;
    }

    // For larger vectors, use OpenMP to parallelize chunk processing
    const size_t chunk_size = 100;
    #pragma omp parallel for schedule(static)
    for (size_t i = 0; i < n; i += chunk_size) {
        size_t end = std::min(i + chunk_size, n);
        for (size_t j = i; j < end; ++j) {
            if (x[j] == target) {
                return true;
            }
        }
    }
    return false;
}
