                for (size_t k = 0; k < i; ++k) {
                    u_val -= L[i * N + k] * U[k * N + j];
                }
                U[i * N + j] = u_val;
            }
            if (i > j) {
                double l_val = fullA[i * N + j];
                for (size_t k = 0; k < j; ++k) {
                    l_val -= L[i * N + k] * U[k * N + j];
                }
                l_val /= U[j * N + j];
                L[i * N + j] = l_val;
            }
        }
        L[i * N + i] = 1.0;
