        double localMin = std::numeric_limits<double>::max();
        for (size_t j = i + 1; j < points.size() - 1; j++) {
            for (size_t k = j + 1; k < points.size(); k++) {
                const double area = triArea(points[i], points[j], points[k]);
                if (area < localMin) {
                    localMin = area;
                }
            }
