size_t findClosestToPi(std::vector<double> const& x) {
    size_t index = 0;
    double min_diff = std::abs(x[0] - M_PI);
    const size_t n = x.size();

    if (n == 1) {
        return 0;
    }

    size_t best_index = 0;
    double best_diff = min_diff;

    #pragma omp parallel
    {
        size_t thread_id = omp_get_thread_num();
        size_t num_threads = omp_get_num_threads();
        size_t start = 1 + thread_id * (n - 1) / num_threads;
        size_t end = 1 + (thread_id + 1) * (n - 1) / num_threads;

        double local_min = std::numeric_limits<double>::max();
        size_t local_index = 0;

        for (size_t i = start; i < end && i < n; ++i) {
            double diff = std::abs(x[i] - M_PI);
            if (diff < local_min) {
                local_min = diff;
                local_index = i;
            }
        }

        #pragma omp critical
        {
            if (local_min < best_diff) {
                best_diff = local_min;
                best_index = local_index;
            }
        }
    }

    return best_index;
}
