        return 0.0;
    }

    std::vector<Point> pointsSorted = points;
    std::sort(pointsSorted.begin(), pointsSorted.end(), [](const Point& a, const Point& b) {
        return a.x < b.x || (a.x == b.x && a.y < b.y);
    });

    int n = points.size();
    std::vector<Point> ans(2 * n);
    int k = 0;

    // Build lower hull
    for (int i = 0; i < n; ++i) {
        while (k >= 2) {
            double x1 = ans[k-1].x - ans[k-2].x;
            double y1 = ans[k-1].y - ans[k-2].y;
            double x2 = pointsSorted[i].x - ans[k-2].x;
            double y2 = pointsSorted[i].y - ans[k-2].y;
            if (x1 * y2 <= y1 * x2) {
                k--;
            } else {
                break;
            }
        }
        ans[k++] = pointsSorted[i];
    }

    // Build upper hull
    for (int i = n - 1, t = k + 1; i > 0; --i) {
        while (k >= t) {
            double x1 = ans[k-1].x - ans[k-2].x;
            double y1 = ans[k-1].y - ans[k-2].y;
            double x2 = pointsSorted[i-1].x - ans[k-2].x;
            double y2 = pointsSorted[i-1].y - ans[k-2].y;
            if (x1 * y2 <= y1 * x2) {
                k--;
            } else {
                break;
            }
        }
        ans[k++] = pointsSorted[i-1];
    }

    ans.resize(k - 1);

    double perimeter = 0.0;
    for (int i = 0; i < ans.size() - 1; ++i) {
        double dx = ans[i+1].x - ans[i].x;
        double dy = ans[i+1].y - ans[i].y;
        perimeter += std::sqrt(dx*dx + dy*dy);
    }
    double dx = ans[0].x - ans.back().x;
    double dy = ans[0].y - ans.back().y;
    perimeter += std::sqrt(dx*dx + dy*dy);

    return perimeter;
}
