int shortestPathLength(std::vector<int> const& A, size_t N, int source, int dest) {
    std::vector<bool> visited(N, false);
    std::vector<std::vector<int>> adj(N);
    for (int i = 0; i < N; ++i) {
        for (int j = 0; j < N; ++j) {
            if (A[i * N + j] != 0) {
                adj[i].push_back(j);
            }
        }
    }

    std::queue<int> q;
    visited[source] = true;
    q.push(source);
    int level = 0;

    while (!q.empty()) {
        int size = q.size();
        for (int i = 0; i < size; ++i) {
            int current = q.front();
            q.pop();
            if (current == dest) {
                return level;
            }
            for (int neighbor : adj[current]) {
                if (!visited[neighbor]) {
                    visited[neighbor] = true;
                    q.push(neighbor);
                }
            }
        }
        ++level;
    }
    return std::numeric_limits<int>::max();
}
