      double maxEl = std::abs(dense[i * N + i]);
      size_t maxRow = i;
      for (size_t k = i + 1; k < N; ++k) {
         double el = std::abs(dense[k * N + i]);
         if (el > maxEl) {
            maxEl = el;
            maxRow = k;
         }
      }

      for (size_t j = i; j < N; ++j) {
         std::swap(dense[maxRow * N + j], dense[i * N + j]);
      }
      std::swap(b_copy[maxRow], b_copy[i]);

      for (size_t k = i + 1; k < N; ++k) {
         double c = -dense[k * N + i] / dense[i * N + i];
         for (size_t j = i; j < N; ++j) {
            dense[k * N + j] += c * dense[i * N + j];
         }
         b_copy[k] += c * b_copy[i];
      }
   }

   for (int i = N - 1; i >= 0; --i) {
      x[i] = b_copy[i] / dense[i * N + i];
      for (int k = i - 1; k >= 0; --k) {
         b_copy[k] -= dense[k * N + i] * x[i];
      }
   }
