void solveLinearSystem(std::vector<COOElement> const& A, std::vector<double> const& b, std::vector<double> &x, size_t N) {
   std::vector<double> matrix(N * N, 0.0);

   for (const auto& element : A) {
      size_t idx = element.row * N + element.column;
      matrix[idx] = element.value;
   }

   std::vector<double> b_copy = b;
   x.assign(N, 0.0);

   for (size_t i = 0; i < N; ++i) {
      double maxEl = std::abs(matrix[i * N + i]);
      size_t maxRow = i;
      for (size_t k = i + 1; k < N; ++k) {
         double val = matrix[k * N + i];
         if (std::abs(val) > maxEl) {
            maxEl = std::abs(val);
            maxRow = k;
         }
      }

      for (size_t k = i; k < N; ++k) {
         std::swap(matrix[maxRow * N + k], matrix[i * N + k]);
      }
      std::swap(b_copy[maxRow], b_copy[i]);

      for (size_t k = i + 1; k < N; ++k) {
         double c = -matrix[k * N + i] / matrix[i * N + i];
         size_t idx_i = i * N;
         size_t idx_k = k * N;
         for (size_t j = i; j < N; ++j) {
            size_t idx_i_j = idx_i + j;
            size_t idx_k_j = idx_k + j;
            if (j == i) {
               matrix[idx_k_j] = 0.0;
            } else {
               matrix[idx_k_j] += c * matrix[idx_i_j];
            }
         }
         b_copy[k] += c * b_copy[i];
      }
   }

   for (int i = N - 1; i >= 0; --i) {
      x[i] = b_copy[i] / matrix[i * N + i];
      for (int k = i - 1; k >= 0; --k) {
         b_copy[k] -= matrix[k * N + i] * x[i];
      }
   }
}
