void convexHull(std::vector<Point> const& points, std::vector<Point> &hull) {
    if (points.size() < 3) {
        hull = points;
        return;
    }

    std::vector<Point> pointsSorted = points;
    std::sort(pointsSorted.begin(), pointsSorted.end(), [](const Point& a, const Point& b) {
        return a.x < b.x || (a.x == b.x && a.y < b.y);
    });

    int n = points.size();
    std::vector<Point> ans(2 * n);

    int k = 0;

    // Build lower hull
    for (int i = 0; i < n; ++i) {
        Point current = pointsSorted[i];
        while (k >= 2) {
            double dx1 = ans[k-1].x - ans[k-2].x;
            double dy1 = ans[k-1].y - ans[k-2].y;
            double dx2 = current.x - ans[k-2].x;
            double dy2 = current.y - ans[k-2].y;
            double cross = dx1 * dy2 - dy1 * dx2;
            if (cross <= 0) {
                k--;
            } else {
                break;
            }
        }
        ans[k++] = current;
    }

    // Build upper hull
    for (int i = n - 1, t = k + 1; i > 0; --i) {
        Point current = pointsSorted[i - 1];
        while (k >= t) {
