   output: [[1.4, 1.8, 2.4],[1.8, 3, 2.2], [1.6, 2.2, 2.4]]
*/
void jacobi2D(std::vector<double> const& input, std::vector<double> &output, size_t N) {
    #pragma omp parallel for schedule(static)
    for (size_t i = 0; i < N; i++) {
        size_t row_start = i * N;
        for (size_t j = 0; j < N; j++) {
            double sum = 0;
            if (i > 0) {
                sum += input[row_start - N + j];
            }
            if (i < N - 1) {
                sum += input[row_start + N + j];
            }
            if (j > 0) {
                sum += input[row_start + j - 1];
            }
            if (j < N - 1) {
                sum += input[row_start + j + 1];
            }
            sum += input[row_start + j];
            output[row_start + j] = sum / 5.0;
        }
