void solveLinearSystem(std::vector<double> const& A, std::vector<double> const& b, std::vector<double> &x, size_t N) {
   std::vector<double> A_copy = A;
   std::vector<double> b_copy = b;

   // Gaussian elimination
   for (size_t i = 0; i < N - 1; i++) {
      double pivot = A_copy[i * N + i];
      if (pivot == 0) {
         return;
      }

      double *row_i = A_copy.data() + i * N;
      for (size_t j = i + 1; j < N; j++) {
         double *row_j = A_copy.data() + j * N;
         double factor = row_j[i] / pivot;

         // Unrolled inner loop for k from i to N-1
         for (size_t k = i; k < N; k += 4) {
            row_j[k] -= factor * row_i[k];
            if (k + 1 < N) {
               row_j[k + 1] -= factor * row_i[k + 1];
            }
            if (k + 2 < N) {
               row_j[k + 2] -= factor * row_i[k + 2];
            }
            if (k + 3 < N) {
               row_j[k + 3] -= factor * row_i[k + 3];
            }
         }

         b_copy[j] -= factor * b_copy[i];
      }
   }

   // Back substitution
   for (int i = N - 1; i >= 0; i--) {
      double sum = 0.0;
      double *x_ptr = x.data();
      double *A_row = A_copy.data() + i * N;
      for (size_t j = i + 1; j < N; j++) {
         sum += A_row[j] * x_ptr[j];
      }
      x[i] = (b_copy[i] - sum) / A_row[i];
   }
}
