void sortIgnoreZero(std::vector<int> &x) {
    size_t nonZeroCount = 0;
    for (int num : x) {
        if (num != 0) {
            nonZeroCount++;
        }
    }
    std::vector<int> nonZeroElements(nonZeroCount);
    size_t index = 0;
    for (int num : x) {
        if (num != 0) {
            nonZeroElements[index++] = num;
        }
    }
    std::sort(nonZeroElements.begin(), nonZeroElements.end());
    size_t nonZeroIndex = 0;
    for (int i = 0; i < x.size(); ++i) {
        if (x[i] != 0) {
            x[i] = nonZeroElements[nonZeroIndex];
