bool xorContains(std::vector<int> const& x, std::vector<int> const& y, int val) {
    bool foundInX = false;
    for (auto it = x.begin(); it != x.end(); ++it) {
        if (*it == val) {
            foundInX = true;
            break;
        }
    }
    bool foundInY = false;
    for (auto it = y.begin(); it != y.end(); ++it) {
        if (*it == val) {
            foundInY = true;
            break;
        }
    }
    return foundInX ^ foundInY;
}
