        Point p = pointsSorted[i];
        while (k >= 2) {
            double dx1 = ans[k - 1].x - ans[k - 2].x;
            double dy1 = ans[k - 1].y - ans[k - 2].y;
            double dx2 = p.x - ans[k - 2].x;
            double dy2 = p.y - ans[k - 2].y;
            if (dx1 * dy2 <= dy1 * dx2) {
                k--;
            } else {
                break;
            }
        }
        ans[k++] = p;
    }

    // Build upper hull
    for (int i = n - 1, t = k + 1; i > 0; --i) {
        Point p = pointsSorted[i - 1];
        while (k >= t) {
            double dx1 = ans[k - 1].x - ans[k - 2].x;
            double dy1 = ans[k - 1].y - ans[k - 2].y;
            double dx2 = p.x - ans[k - 2].x;
