    std::vector<std::vector<double>> matrix(N, std::vector<double>(N, 0.0));
    std::vector<double> b_copy = b;
    for (const auto& element : A) {
        matrix[element.row][element.column] = element.value;
    }
    x.assign(N, 0.0);

    for (size_t i = 0; i < N; ++i) {
        double maxEl_sq = matrix[i][i] * matrix[i][i];
        size_t maxRow = i;
        for (size_t k = i + 1; k < N; ++k) {
            double val = matrix[k][i];
            double val_sq = val * val;
            if (val_sq > maxEl_sq) {
                maxEl_sq = val_sq;
                maxRow = k;
            }
        }

        for (size_t k = i; k < N; ++k) {
            std::swap(matrix[maxRow][k], matrix[i][k]);
        }
        std::swap(b_copy[maxRow], b_copy[i]);

        double invPivot = 1.0 / matrix[i][i];
        for (size_t k = i + 1; k < N; ++k) {
            double c = -matrix[k][i] * invPivot;
            matrix[k][i] = 0.0;
            for (size_t j = i + 1; j < N; ++j) {
                matrix[k][j] += c * matrix[i][j];
            }
            b_copy[k] += c * b_copy[i];
        }
    }

    for (int i = N - 1; --i >= 0;) {
        double invPivot = 1.0 / matrix[i][i];
        x[i] = b_copy[i] * invPivot;
        double x_i = x[i];
        for (int k = i - 1; k >= 0; --k) {
            b_copy[k] -= matrix[k][i] * x_i;
        }
    }
}
