#pragma once
#include <vector>
#include <omp.h>

struct Book {
    std::string title;
    int pages;
};

size_t findLastShortBook(std::vector<Book> const& books) {
    size_t result = books.size();
    #pragma omp parallel for reduction(max : result)
    for (size_t i = 0; i < books.size(); i++) {
        if (books[i].pages < 100) {
            if (i > result) {
                result = i;
            }
        }
    }
    return result;
}
