                unsigned int b = a + k;
                std::complex<double> t = x_copy[a] - x_copy[b];
                x_copy[a] += x_copy[b];
                x_copy[b] = t * T;
            }
            T *= phiT;
        }
    }
    // Decimate
    unsigned int m = (unsigned int)std::log2(N);
    #pragma omp parallel for
    for (unsigned int a = 0; a < N; a++) {
        unsigned int b = a;
        // Reverse bits
        b = (((b & 0xaaaaaaaa) >> 1) | ((b & 0x55555555) << 1));
