      if (start < n) {
         local_best = start;
         local_best_diff = std::abs(x[start] - M_PI);
      }

      for (size_t i = start; i < end; ++i) {
         double diff = std::abs(x[i] - M_PI);
         if (diff < local_best_diff) {
            local_best_diff = diff;
            local_best = i;
         }
      }

      #pragma omp critical
      {
         if (local_best_diff < best_diff) {
            best_diff = local_best_diff;
            best_index = local_best;
         }
      }
   }

   return best_index;
}
