    if (n == 0) return false;
    bool found = false;
    #pragma omp parallel
    {
        int num_threads = omp_get_num_threads();
        int chunk_size = (n + num_threads - 1) / num_threads;
        int start = omp_get_thread_num() * chunk_size;
        int end = start + chunk_size;
        if (end > n) end = n;
        for (int i = start; i < end; i++) {
            if (x[i] == target) {
                found = true;
            }
        }
    }
