void solveLinearSystem(std::vector<COOElement> const& A, std::vector<double> const& b, std::vector<double> &x, size_t N) {
   std::vector<double> matrix1d(N * N, 0.0);
   for (const auto& element : A) {
      size_t idx = element.row * N + element.column;
      matrix1d[idx] = element.value;
   }
   std::vector<double> b_copy = b;
   x.assign(N, 0.0);

   for (size_t i = 0; i < N; ++i) {
      double maxEl = std::abs(matrix1d[i * N + i]);
      size_t maxRow = i;
      for (size_t k = i + 1; k < N; ++k) {
         double val = std::abs(matrix1d[k * N + i]);
         if (val > maxEl) {
            maxEl = val;
            maxRow = k;
         }
      }

      for (size_t k = i; k < N; ++k) {
         size_t idx1 = i * N + k;
         size_t idx2 = maxRow * N + k;
         std::swap(matrix1d[idx1], matrix1d[idx2]);
      }
      std::swap(b_copy[maxRow], b_copy[i]);

      double pivot = matrix1d[i * N + i];
      for (size_t k = i + 1; k < N; ++k) {
         double c = -matrix1d[k * N + i] / pivot;
         matrix1d[k * N + i] = 0.0;
         for (size_t j = i + 1; j < N; ++j) {
            matrix1d[k * N + j] += c * matrix1d[i * N + j];
         }
         b_copy[k] += c * b_copy[i];
      }
   }

   for (int i = N - 1; i >= 0; --i) {
      x[i] = b_copy[i] / matrix1d[i * N + i];
      for (int k = i - 1; k >= 0; --k) {
         b_copy[k] -= matrix1d[k * N + i] * x[i];
      }
   }
}
