#pragma once
#include <vector>
#include <algorithm>
#include <utility>

struct COOElement {
   size_t row, column;
   double value;
};

void luFactorize(std::vector<COOElement> const& A, std::vector<double> &L, std::vector<double> &U, size_t N) {
   std::vector<std::vector<std::pair<size_t, double>>> A_rows(N);
   for (const auto& elem : A) {
      A_rows[elem.row].push_back({elem.column, elem.value});
   }
   for (size_t i = 0; i < N; ++i) {
      std::sort(A_rows[i].begin(), A_rows[i].end());
   }

   auto getA = [&](size_t i, size_t j) -> double {
      auto it = std::lower_bound(A_rows[i].begin(), A_rows[i].end(), std::make_pair(j, 0.0));
      if (it != A_rows[i].end() && it->first == j) {
         return it->second;
      }
      return 0.0;
   };

   for (size_t i = 0; i < N; ++i) {
      for (size_t j = 0; j < N; ++j) {
         if (j >= i) {
            double val = getA(i, j);
            U[i * N + j] = val;
            for (size_t k = 0; k < i; ++k) {
               U[i * N + j] -= L[i * N + k] * U[k * N + j];
            }
         }
         if (i > j) {
            double val = getA(i, j);
            L[i * N + j] = val;
            for (size_t k = 0; k < j; ++k) {
               L[i * N + j] -= L[i * N + k] * U[k * N + j];
            }
            L[i * N + j] /= U[j * N + j];
         }
      }
      L[i * N + i] = 1;
   }
}
