    for (size_t i = 0; i < N; i++) {
        for (size_t j = 0; j < N; j++) {
            int sum = 0;
            if (i > 0) {
                sum += input[(i - 1) * N + j];
            }
            if (i < N - 1) {
                sum += input[(i + 1) * N + j];
            }
            if (j > 0) {
                sum += input[i * N + (j - 1)];
            }
            if (j < N - 1) {
                sum += input[i * N + (j + 1)];
            }
            if (i > 0 && j > 0) {
                sum += input[(i - 1) * N + (j - 1)];
            }
            if (i > 0 && j < N - 1) {
                sum += input[(i - 1) * N + (j + 1)];
            }
            if (i < N - 1 && j > 0) {
                sum += input[(i + 1) * N + (j - 1)];
            }
            if (i < N - 1 && j < N - 1) {
                sum += input[(i + 1) * N + (j + 1)];
            }
            if (input[i * N + j] == 1) {
                if (sum < 2) {
                    output[i * N + j] = 0;
                } else if (sum == 2 || sum == 3) {
                    output[i * N + j] = 1;
                } else {
                    output[i * N + j] = 0;
