#include <omp.h>

/* Return the index of the value in the vector x that is closest to the math constant PI.
   Use M_PI for the value of PI.
   Example:
   input: [9.18, 3.05, 7.24, 11.3, -166.49, 2.1]
   output: 1
*/
size_t findClosestToPi(std::vector<double> const& x) {
   size_t best_index = 0;
   double best_diff = std::abs(x[0] - M_PI);

   #pragma omp parallel
   {
      size_t local_best = 0;
      double local_best_diff = best_diff;

      #pragma omp for
      for (size_t i = 1; i < x.size(); ++i) {
         double diff = std::abs(x[i] - M_PI);
         if (diff < local_best_diff) {
            local_best_diff = diff;
            local_best = i;
         }
      }

      #pragma omp critical
      {
         if (local_best_diff < best_diff) {
            best_diff = local_best_diff;
            best_index = local_best;
         }
      }
   }

   return best_index;
}
