int shortestPathLength(std::vector<int> const& A, size_t N, int source, int dest) {
    std::vector<std::vector<int>> adj(N);
    for (int i = 0; i < N; ++i) {
        for (int j = 0; j < N; ++j) {
            if (A[i * N + j]) {
                adj[i].push_back(j);
            }
        }
    }

    std::vector<int> dist(N, -1);
    std::queue<int> q;
    q.push(source);
    dist[source] = 0;

    while (!q.empty()) {
        int u = q.front();
        q.pop();
        if (u == dest) {
            return dist[u];
        }
        for (int v : adj[u]) {
            if (dist[v] == -1) {
                dist[v] = dist[u] + 1;
                q.push(v);
            }
        }
    }
    return std::numeric_limits<int>::max();
}
