#pragma once
#include <vector>
#include <omp.h>
size_t findClosestToPi(std::vector<double> const& x) {
    size_t best_index = 0;
    double best_diff = std::abs(x[0] - M_PI);
    #pragma omp parallel for
    for (size_t i = 1; i < x.size(); ++i) {
        double diff = std::abs(x[i] - M_PI);
        if (diff < best_diff) {
            #pragma omp critical
            {
                best_diff = diff;
                best_index = i;
            }
        }
    }
    return best_index;
}
