#include <vector>
#include <cstddef>

bool xorContains(std::vector<int> const& x, std::vector<int> const& y, int val) {
    bool foundInX = false;
    for (size_t i = 0; i < x.size(); ++i) {
        if (x[i] == val) {
            foundInX = true;
            break;
        }
    }
    bool foundInY = false;
    for (size_t i = 0; i < y.size(); ++i) {
        if (y[i] == val) {
            foundInY = true;
            break;
        }
    }
    return foundInX ^ foundInY;
}
