struct Book {
    std::string title;
    int pages;
};

size_t findLastShortBook(std::vector<Book> const& books) {
    size_t last_index = books.size();
    #pragma omp parallel for reduction(max: last_index)
    for (size_t i = 0; i < books.size(); i++) {
        if (books[i].pages < 100) {
            last_index = i;
        }
    }
    return last_index;
}
