#include <omp.h>

struct COOElement {
   size_t row, column;
   double value;
};

void solveLinearSystem(std::vector<COOElement> const& A, std::vector<double> const& b, std::vector<double> &x, size_t N) {
   std::vector<double> matrix_1d(N * N, 0.0);
   std::vector<double> b_copy = b;

   for (const auto& element : A) {
      size_t idx = element.row * N + element.column;
      matrix_1d[idx] = element.value;
   }

   x.assign(N, 0.0);

   for (size_t i = 0; i < N; ++i) {
      double maxEl = std::abs(matrix_1d[i * N + i]);
      size_t maxRow = i;
      for (size_t k = i + 1; k < N; ++k) {
         double val = std::abs(matrix_1d[k * N + i]);
         if (val > maxEl) {
            maxEl = val;
            maxRow = k;
         }
      }

      for (size_t k = i; k < N; ++k) {
         std::swap(matrix_1d[maxRow * N + k], matrix_1d[i * N + k]);
      }
      std::swap(b_copy[maxRow], b_copy[i]);

      #pragma omp parallel for
      for (size_t k = i + 1; k < N; ++k) {
         double c = -matrix_1d[k * N + i] / matrix_1d[i * N + i];
         for (size_t j = i; j < N; ++j) {
            size_t idx = k * N + j;
            if (i == j) {
               matrix_1d[idx] = 0.0;
            } else {
               matrix_1d[idx] += c * matrix_1d[i * N + j];
            }
         }
         b_copy[k] += c * b_copy[i];
      }
   }

   for (int i = N - 1; i >= 0; --i) {
      x[i] = b_copy[i] / matrix_1d[i * N + i];
      for (int k = i - 1; k >= 0; --k) {
         b_copy[k] -= matrix_1d[k * N + i] * x[i];
      }
   }
}
