   fft_helper( x );
   // conjugate the complex numbers again
   std::transform(x.begin(), x.end(), x.begin(), [](auto const& val) { return std::conj(val); });
   // scale the numbers
   std::transform(x.begin(), x.end(), x.begin(), [&](std::complex<double> c) { return c / static_cast<double>(x.size()); });
