void solveLinearSystem(std::vector<double> const& A, std::vector<double> const& b, std::vector<double> &x, size_t N) {
   std::vector<double> A_copy = A;
   std::vector<double> b_copy = b;
   double *A_ptr = A_copy.data();
   double *b_ptr = b_copy.data();

   for (size_t i = 0; i < N - 1; i++) {
      double pivot = A_ptr[i * N + i];
      if (pivot == 0) {
         return;
      }
      for (size_t j = i + 1; j < N; j++) {
         double factor = A_ptr[j * N + i] / pivot;
         double *row_i = A_ptr + i * N;
         double *row_j = A_ptr + j * N;
         for (size_t k = i; k < N; k++) {
            row_j[k] -= factor * row_i[k];
         }
         b_ptr[j] -= factor * b_ptr[i];
      }
   }

   for (int i = N - 1; i >= 0; i--) {
      double sum = 0;
      for (size_t j = i + 1; j < N; j++) {
         sum += A_ptr[i * N + j] * x[j];
      }
      x[i] = (b_ptr[i] - sum) / A_ptr[i * N + i];
   }
}
