void solveLinearSystem(std::vector<COOElement> const& A, std::vector<double> const& b, std::vector<double> &x, size_t N) {
   std::vector<double> matrix_flat(N * N, 0.0);
   for (const auto& element : A) {
      matrix_flat[element.row * N + element.column] = element.value;
   }
   std::vector<double> b_copy = b;
   x.assign(N, 0.0);

   for (size_t i = 0; i < N; ++i) {
      double maxEl = std::abs(matrix_flat[i * N + i]);
      size_t maxRow = i;
      for (size_t k = i + 1; k < N; ++k) {
         double current = std::abs(matrix_flat[k * N + i]);
         if (current > maxEl) {
            maxEl = current;
            maxRow = k;
         }
      }

      for (size_t k = i; k < N; ++k) {
         std::swap(matrix_flat[i * N + k], matrix_flat[maxRow * N + k]);
      }
      std::swap(b_copy[maxRow], b_copy[i]);

      for (size_t k = i + 1; k < N; ++k) {
         double c = -matrix_flat[k * N + i] / matrix_flat[i * N + i];
         for (size_t j = i; j < N; ++j) {
            if (j == i) {
               matrix_flat[k * N + j] = 0.0;
            } else {
               matrix_flat[k * N + j] += c * matrix_flat[i * N + j];
            }
         }
         b_copy[k] += c * b_copy[i];
      }
   }

   for (int i = N - 1; i >= 0; --i) {
      x[i] = b_copy[i] / matrix_flat[i * N + i];
      for (int k = i - 1; k >= 0; --k) {
         b_copy[k] -= matrix_flat[k * N + i] * x[i];
      }
   }
}
