    std::vector<Point> pointsSorted = points;
    std::sort(pointsSorted.begin(), pointsSorted.end(), [](const Point& a, const Point& b) {
        return a.x < b.x || (a.x == b.x && a.y < b.y);
    });

    int n = points.size();
    int k = 0;
    std::vector<Point> ans(2 * n);

    // Build lower hull
    for (int i = 0; i < n; ++i) {
        while (k >= 2) {
            double cross = (ans[k-1].x - ans[k-2].x) * (pointsSorted[i].y - ans[k-2].y)
                           - (ans[k-1].y - ans[k-2].y) * (pointsSorted[i].x - ans[k-2].x);
            if (cross <= 0) {
                k--;
            } else {
                break;
            }
        }
        ans[k++] = pointsSorted[i];
    }

    // Build upper hull
    for (int i = n - 1, t = k + 1; i > 0; --i) {
        while (k >= t) {
            double cross = (ans[k-1].x - ans[k-2].x) * (pointsSorted[i-1].y - ans[k-2].y)
                           - (ans[k-1].y - ans[k-2].y) * (pointsSorted[i-1].x - ans[k-2].x);
            if (cross <= 0) {
                k--;
            } else {
                break;
            }
        }
        ans[k++] = pointsSorted[i-1];
    }

    ans.resize(k - 1);
    int m = ans.size();
    double perimeter = 0.0;

    for (int i = 0; i < m - 1; ++i) {
        double dx = ans[i+1].x - ans[i].x;
        double dy = ans[i+1].y - ans[i].y;
        perimeter += std::sqrt(dx * dx + dy * dy);
    }

    double dx = ans[0].x - ans[m-1].x;
    double dy = ans[0].y - ans[m-1].y;
    perimeter += std::sqrt(dx * dx + dy * dy);

    return perimeter;
}
