size_t findClosestToPi(std::vector<double> const& x) {
    size_t best_index = 0;
    double best_diff = std::abs(x[0] - M_PI);

    const size_t num_threads = omp_get_num_threads();
    const size_t chunk_size = (x.size() + num_threads - 1) / num_threads;

    #pragma omp parallel
    {
        size_t thread_id = omp_get_thread_num();
        size_t start = thread_id * chunk_size;
        size_t end = std::min(start + chunk_size, x.size());

        size_t local_best_index = start;
        double local_best_diff = std::abs(x[start] - M_PI);

        for (size_t i = start; i < end; ++i) {
            double diff = std::abs(x[i] - M_PI);
            if (diff < local_best_diff) {
                local_best_diff = diff;
                local_best_index = i;
            }
        }

        #pragma omp critical
        {
            if (local_best_diff < best_diff) {
                best_diff = local_best_diff;
                best_index = local_best_index;
            }
        }
    }

    return best_index;
}
