#include <vector>

void kernel_durbin(int n,
                   std::vector<double>& r,
                   std::vector<double>& y) {

    y[0] = -r[0];
    double beta = 1.0;
    double alpha = -r[0];

    for (int k = 1; k < n; k++) {
        beta = (1.0 - alpha * alpha) * beta;
        double sum = 0.0;

        for (int i = 0; i < k; i += 4) {
            sum += r[k - i - 1] * y[i];
            if (i + 1 < k) sum += r[k - i - 2] * y[i + 1];
            if (i + 2 < k) sum += r[k - i - 3] * y[i + 2];
            if (i + 3 < k) sum += r[k - i - 4] * y[i + 3];
        }

        alpha = -(r[k] + sum) / beta;

        for (int i = 0; i < k; i += 4) {
            y[i] += alpha * y[k - i - 1];
            if (i + 1 < k) y[i + 1] += alpha * y[k - i - 2];
            if (i + 2 < k) y[i + 2] += alpha * y[k - i - 3];
            if (i + 3 < k) y[i + 3] += alpha * y[k - i - 4];
        }

        y[k] = alpha;
    }
}
