void kernel_nussinov(int n, std::vector<int>& seq,
                     std::vector<std::vector<int>>& table) {

    #pragma omp parallel for schedule(static)
    for (int i = n - 1; i >= 0; i--) {
        for (int j = i + 1; j < n; j++) {
            table[i][j] = std::max(table[i][j], table[i][j-1]);

            if (i + 1 < n) {
                table[i][j] = std::max(table[i][j], table[i+1][j]);
            }

            if (i + 1 < n) {
                if (i < j - 1) {
                    int m = (seq[i] + seq[j] == 3) ? 1 : 0;
                    table[i][j] = std::max(table[i][j], table[i+1][j-1] + m);
                } else {
                    table[i][j] = std::max(table[i][j], table[i+1][j-1]);
                }
            }

            for (int k = i + 1; k < j; k++) {
                table[i][j] = std::max(table[i][j], table[i][k] + table[k+1][j]);
