void kernel_gramschmidt(int m, int n,
                        std::vector<std::vector<double>>& A,
                        std::vector<std::vector<double>>& R,
                        std::vector<std::vector<double>>& Q) {
    for (int k = 0; k < n; k++) {
        double nrm = 0.0;
        #pragma omp simd
        for (int i = 0; i < m; i++) {
            nrm += A[i][k] * A[i][k];
        }
        R[k][k] = std::sqrt(nrm);
        #pragma omp simd
        for (int i = 0; i < m; i++) {
            Q[i][k] = A[i][k] / R[k][k];
        }
        #pragma omp parallel for
        for (int j = k + 1; j < n; j++) {
            double sum = 0.0;
            #pragma omp simd
            for (int i = 0; i < m; i++) {
                sum += Q[i][k] * A[i][j];
