            ym2 = ym1;
            ym1 = y1[i][j];
        }
    }

    #pragma omp parallel for
    for (int i = 0; i < w; i++) {
        float yp1 = 0.0;
        float yp2 = 0.0;
        float xp1 = 0.0;
        float xp2 = 0.0;
        for (int j = h - 1; j >= 0; j--) {
            y2[i][j] = a3 * xp1 + a4 * xp2 + b1 * yp1 + b2 * yp2;
            xp2 = xp1;
            xp1 = imgIn[i][j];
            yp2 = yp1;
            yp1 = y2[i][j];
        }
    }

    #pragma omp parallel for
    for (int i = 0; i < w; i++) {
        for (int j = 0; j < h; j++) {
            imgOut[i][j] = c1 * (y1[i][j] + y2[i][j]);
