void kernel_trisolv(int n,
                    std::vector<std::vector<double>>& L,
                    std::vector<double>& x,
                    std::vector<double>& b) {
    for (int i = 0; i < n; i++) {
        auto& l_row = L[i];
        double sum = b[i];
        for (int j = 0; j < i; j++) {
            sum -= l_row[j] * x[j];
