        for (int j = 0; j < m; j++) {
            data[i][j] -= mean[j];
        }
    }

    #pragma omp parallel for
    for (int i = 0; i < m; i++) {
        for (int j = i; j < m; j++) {
            cov[i][j] = 0.0;
            for (int k = 0; k < n; k++) {
                cov[i][j] += data[k][i] * data[k][j];
