#include <vector>

void kernel_nussinov(int n, std::vector<int>& seq,
                     std::vector<std::vector<int>>& table) {
    auto match = [](int b1, int b2) {
        return b1 + b2 == 3 ? 1 : 0;
    };
    for (int i = n - 1; i >= 0; i--) {
        for (int j = i + 1; j < n; j++) {
            if (table[i][j] < table[i][j - 1]) {
                table[i][j] = table[i][j - 1];
            }
            if (table[i][j] < table[i + 1][j]) {
                table[i][j] = table[i + 1][j];
            }
            if (i < j - 1) {
                int candidate = table[i + 1][j - 1] + match(seq[i], seq[j]);
                if (candidate > table[i][j]) {
                    table[i][j] = candidate;
                }
            } else {
                if (table[i + 1][j - 1] > table[i][j]) {
                    table[i][j] = table[i + 1][j - 1];
                }
            }
            int current = table[i][j];
            for (int k = i + 1; k < j; k++) {
                int candidate = table[i][k] + table[k + 1][j];
                if (candidate > current) {
                    current = candidate;
                }
            }
            table[i][j] = current;
        }
    }
}
