void kernel_cholesky(int n,
                     std::vector<std::vector<double>>& A) {
    for (int i = 0; i < n; i++) {
        std::vector<double>& row_i = A[i];
        for (int j = 0; j < i; j++) {
            std::vector<double>& row_j = A[j];
            double sum = 0.0;
            for (int k = 0; k < j; k++) {
                sum -= row_i[k] * row_j[k];
