void kernel_nussinov(int n, std::vector<int>& seq,
                     std::vector<std::vector<int>>& table) {
    for (int i = n - 1; i >= 0; i--) {
        for (int j = i + 1; j < n; j++) {
            table[i][j] = (table[i][j] > table[i][j-1]) ? table[i][j] : table[i][j-1];
            if (i + 1 < n) {
                table[i][j] = (table[i][j] > table[i+1][j]) ? table[i][j] : table[i+1][j];
            }
            if (i + 1 < n) {
                int val = table[i+1][j-1];
                if (i < j - 1) {
                    val += (seq[i] + seq[j] == 3) ? 1 : 0;
                }
                table[i][j] = (table[i][j] > val) ? table[i][j] : val;
            }
            for (int k = i + 1; k < j; k++) {
                int sum = table[i][k] + table[k+1][j];
                table[i][j] = (table[i][j] > sum) ? table[i][j] : sum;
