void kernel_cholesky(int n,
                     std::vector<std::vector<double>>& A) {
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < i; j++) {
            double sum = 0.0;
            for (int k = 0; k < j; k++) {
                sum += A[i][k] * A[j][k];
            }
            A[i][j] -= sum;
            A[i][j] /= A[j][j];
        }
        double sum = 0.0;
        for (int k = 0; k < i; k++) {
            sum += A[i][k] * A[i][k];
        }
        A[i][i] -= sum;
        A[i][i] = std::sqrt(A[i][i]);
