void kernel_gramschmidt(int m, int n,
                        std::vector<std::vector<double>>& A,
                        std::vector<std::vector<double>>& R,
                        std::vector<std::vector<double>>& Q) {
    for (int k = 0; k < n; k++) {
        double nrm = 0.0;
        for (int i = 0; i < m; i += 4) {
            nrm += A[i][k] * A[i][k];
            if (i + 1 < m) nrm += A[i+1][k] * A[i+1][k];
            if (i + 2 < m) nrm += A[i+2][k] * A[i+2][k];
            if (i + 3 < m) nrm += A[i+3][k] * A[i+3][k];
        }
        R[k][k] = std::sqrt(nrm);
        for (int i = 0; i < m; i++) {
            Q[i][k] = A[i][k] / R[k][k];
        }
        for (int j = k + 1; j < n; j++) {
            double r = 0.0;
            for (int i = 0; i < m; i += 4) {
                r += Q[i][k] * A[i][j];
                if (i + 1 < m) r += Q[i+1][k] * A[i+1][j];
                if (i + 2 < m) r += Q[i+2][k] * A[i+2][j];
                if (i + 3 < m) r += Q[i+3][k] * A[i+3][j];
            }
            R[k][j] = r;
            for (int i = 0; i < m; i += 4) {
                A[i][j] -= Q[i][k] * r;
                if (i + 1 < m) A[i+1][j] -= Q[i+1][k] * r;
                if (i + 2 < m) A[i+2][j] -= Q[i+2][k] * r;
                if (i + 3 < m) A[i+3][j] -= Q[i+3][k] * r;
