#include <vector>
#include <omp.h>

void kernel_adi(int tsteps, int n,
        std::vector<std::vector<double>>& u,
        std::vector<std::vector<double>>& v,
        std::vector<std::vector<double>>& p,
        std::vector<std::vector<double>>& q) {
    double DX = 1.0 / n;
    double DY = 1.0 / n;
    double DT = 1.0 / tsteps;
    double B1 = 2.0;
    double B2 = 1.0;
    for (int t = 1; t <= tsteps; t++) {
        double mul1 = B1 * DT / (DX * DX);
        double mul2 = B2 * DT / (DY * DY);
        double a_val = -mul1 / 2.0;
        double b_val = 1.0 + mul1;
        double c_val = a_val;
        double d_val = -mul2 / 2.0;
        double e_val = 1.0 + mul2;
        double f_val = d_val;

        #pragma omp parallel for
        for (int i = 1; i < n - 1; i++) {
            v[0][i] = 1.0;
            p[i][0] = 0.0;
            q[i][0] = v[0][i];
            for (int j = 1; j < n - 1; j++) {
                double denom = a_val * p[i][j-1] + b_val;
                p[i][j] = -c_val / denom;
                double num = -d_val * u[j][i-1] + (1.0 + 2.0 * d_val) * u[j][i] - f_val * u[j][i+1] - a_val * q[i][j-1];
                q[i][j] = num / denom;
            }
            v[n-1][i] = 1.0;
            for (int j = n-2; j >= 1; j--) {
                v[j][i] = p[i][j] * v[j+1][i] + q[i][j];
            }
        }

        #pragma omp parallel for
        for (int i = 1; i < n - 1; i++) {
            u[i][0] = 1.0;
            p[i][0] = 0.0;
            q[i][0] = u[i][0];
            for (int j = 1; j < n - 1; j++) {
                double denom = d_val * p[i][j-1] + e_val;
                p[i][j] = -f_val / denom;
                double num = -a_val * v[i-1][j] + (1.0 + 2.0 * a_val) * v[i][j] - c_val * v[i+1][j] - d_val * q[i][j-1];
                q[i][j] = num / denom;
            }
            u[i][n-1] = 1.0;
            for (int j = n-2; j >= 1; j--) {
                u[i][j] = p[i][j] * u[i][j+1] + q[i][j];
            }
        }
    }
}
