#include <vector>

void kernel_nussinov(int n, std::vector<int>& seq, std::vector<std::vector<int>>& table) {
    for (int i = n - 1; i >= 0; --i) {
        for (int j = i + 1; j < n; ++j) {
            table[i][j] = table[i][j-1];
            if (table[i+1][j] > table[i][j]) 
                table[i][j] = table[i+1][j];
            if (i < j - 1) {
                int match_val = (seq[i] + seq[j] == 3);
                int candidate = table[i+1][j-1] + match_val;
                if (candidate > table[i][j])
                    table[i][j] = candidate;
            } else {
                if (table[i+1][j-1] > table[i][j])
                    table[i][j] = table[i+1][j-1];
            }
            int best = table[i][j];
            for (int k = i + 1; k < j; ++k) {
                int candidate = table[i][k] + table[k+1][j];
                if (candidate > best)
                    best = candidate;
            }
            table[i][j] = best;
        }
    }
}
