#include <cmath>
#include <vector>

void kernel_correlation(int m, int n,
                        std::vector<std::vector<double>>& data,
                        std::vector<std::vector<double>>& corr,
                        std::vector<double>& mean,
                        std::vector<double>& stddev) {
    double eps = 0.1;
    double inv_sqrt_n = 1.0 / std::sqrt(n);

    for (int j = 0; j < m; ++j) {
        double sum = 0.0;
        #pragma omp parallel for
        for (int i = 0; i < n; ++i) {
            sum += data[i][j];
        }
        mean[j] = sum / n;
    }

    for (int j = 0; j < m; ++j) {
        double sum_sq = 0.0;
        #pragma omp parallel for
        for (int i = 0; i < n; ++i) {
            double diff = data[i][j] - mean[j];
            sum_sq += diff * diff;
        }
        stddev[j] = std::sqrt(sum_sq / n);
        stddev[j] = (stddev[j] <= eps) ? 1.0 : stddev[j];
    }

    for (int j = 0; j < m; ++j) {
        double inv_std = 1.0 / stddev[j];
        double scale = inv_std * inv_sqrt_n;
        #pragma omp parallel for
        for (int i = 0; i < n; ++i) {
            data[i][j] -= mean[j];
            data[i][j] *= scale;
        }
    }

    for (int i = 0; i < m - 1; ++i) {
        corr[i][i] = 1.0;
        for (int j = i + 1; j < m; ++j) {
            double sum = 0.0;
            #pragma omp parallel for
            for (int k = 0; k < n; ++k) {
                sum += data[k][i] * data[k][j];
            }
            corr[i][j] = sum;
            corr[j][i] = sum;
        }
    }
    corr[m-1][m-1] = 1.0;
}
