    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            data[i][j] -= mean[j];
        }
    }

    const double inv_n_minus_1 = 1.0 / (n - 1.0);
    for (int i = 0; i < m; ++i) {
        for (int j = i; j < m; ++j) {
            double sum = 0.0;
            #pragma omp parallel for
            for (int k = 0; k < n; ++k) {
                sum += data[k][i] * data[k][j];
            }
            cov[i][j] = sum * inv_n_minus_1;
            cov[j][i] = cov[i][j];
