    float a6 = k * exp_alpha * (alpha - 1.0);
    float a7 = k * exp_alpha * (alpha + 1.0);
    float a8 = -k * exp_alpha2;
    float b1 = std::pow(2.0, -alpha);
    float b2 = -exp_alpha2;
    float c1 = 1.0f;
    float c2 = 1.0f;

